import { useState } from "react";

export default function Form() {
  const [inputValue, setInputValue] = useState({
    currentSaving: 0,
    yearlyContribution: 0,
    expectedReturn: 0,
    duration: 0,
  });
  function handleSubmit(event) {
    event.preventDefault();
  }

  function handleInputChange(inputId, eventValue) {
    setInputValue((prevInputValue) => {
      console.log(prevInputValue);

      return {
        ...prevInputValue,
        [inputId]: eventValue,
      };
    });
  }

  function resetHandler(){

  }
  return (
    <form className="form" onSubmit={handleSubmit}>
      <div className="input-group">
        <p>
          <label htmlFor="current-savings">Current Savings ($)</label>
          <input
            type="number"
            id="currentSavings"
            onChange={(event) =>
              handleInputChange("currentSavings", event.target.value)
            }
          />
        </p>
        <p>
          <label htmlFor="yearly-contribution">Yearly Savings ($)</label>
          <input
            type="number"
            id="yearlyContribution"
            onChange={(event) =>
              handleInputChange("yearlyContribution", event.target.value)
            }
          />
        </p>
      </div>
      <div className="input-group">
        <p>
          <label htmlFor="expectedReturn">
            Expected Interest (%, per year)
          </label>
          <input
            type="number"
            id="expectedReturn"
            onChange={(event) =>
              handleInputChange("expectedReturn", event.target.value)
            }
          />
        </p>
        <p>
          <label htmlFor="duration">Investment Duration (years)</label>
          <input
            type="number"
            id="duration"
            onChange={(event) =>
              handleInputChange("duration", event.target.value)
            }
          />
        </p>
      </div>
      <p className="actions">
        <button type="reset" className="buttonAlt">
          Reset
        </button>
        <button type="submit" className="button">
          Calculate
        </button>
      </p>
    </form>
  );
}
