import { useState } from "react";



export default function Form({onChange, inputValue, handleReset, handleSubmit, }) {

  function handleSubmit(event) {
    event.preventDefault();
    console.log("Submitted Data:", inputValue);
  }


  function submitHandler(){
    console.log('SUBMIT');

  }


  return (
    <form className="form" onSubmit={handleSubmit}>
      <div className="input-group">
        <p>
          <label htmlFor="currentSaving">Current Savings ($)</label>
          <input
            type="number"
            id="currentSaving"
            value={inputValue.currentSaving}
            onChange={(event) =>
              onChange("currentSaving", event.target.value)
            }
          />
        </p>
        <p>
          <label htmlFor="yearlyContribution">Yearly Savings ($)</label>
          <input
            type="number"
            id="yearlyContribution"
            value={inputValue.yearlyContribution}
            onChange={(event) =>
              onChange("yearlyContribution", event.target.value)
            }
          />
        </p>
      </div>
      <div className="input-group">
        <p>
          <label htmlFor="expectedReturn">Expected Interest (%, per year)</label>
          <input
            type="number"
            id="expectedReturn"
            value={inputValue.expectedReturn}
            onChange={(event) =>
              onChange("expectedReturn", event.target.value)
            }
          />
        </p>
        <p>
          <label htmlFor="duration">Investment Duration (years)</label>
          <input
            type="number"
            id="duration"
            value={inputValue.duration}
            onChange={(event) =>
              onChange("duration", event.target.value)
            }
          />
        </p>
      </div>
      <p className="actions">
        <button type="reset" onClick={handleReset} className="buttonAlt">
          Reset
        </button>
        <button type="submit" onClick={submitHandler} className="button" >
          Calculate
        </button>
      </p>
    </form>
  );
}
